    def onClickAdd(self):
        if self.nameBox.text():
            b = brother()
            b.name = self.nameBox.text()
            b.gpa = self.nameBox_2.text()
            b.grade = self.Grade.currentText()
            brother.addBrother(b)
            #self.nameBox.clear()
            #self.nameBox_2.clear()
            self.Grade.setCurrentIndex(0)
            self.createTable()
            self.tableWidget.resizeColumnToContents(0)
        else:
            m = QMessageBox()    
            m.setText('Please enter a name')    
            m.exec()        
    def onCickSched(self):
        startingDate = self.startDate.date().toPyDate()
        endingDate = self.endDate.date().toPyDate()
        brother.getDates(startingDate,endingDate)
        brother.calcNumShifts()
        brother.calcDates()
        brother.scheduler()        

    def createTable(self):
        numrows = len(brothersList)
        self.tableWidget.setRowCount(numrows)
        for row in range(numrows):
            self.tableWidget.setItem(row,0,QTableWidgetItem(brothersList[row].name))
            self.tableWidget.setItem(row,1,QTableWidgetItem(brothersList[row].gpa))
            self.tableWidget.setItem(row,2,QTableWidgetItem(brothersList[row].grade))